import React, { Component } from "react";
import questions from "../resources/quizQuestion.json";

export default class QuizComponent extends Component {
  constructor(props) {
    super();
    this.state = {
      i: 0,
    };
  }
  render() {
    return (
      <div className="container">
        <div className="mainBox">
          <div className="counter">1 of 15</div>
          <h1>Question</h1>
          {/* <p className="question">Which is the only mammal that can jump?</p> */}
          <p className="question">{questions[this.state.i].question}</p>
          <div className="options">
            <button className="mcq btn" id="a">
              {questions[this.state.i].optionA}
            </button>
            <button className="mcq btn" id="b">
              {questions[this.state.i].optionB}
            </button>
            <button className="mcq btn" id="c">
              {questions[this.state.i].optionC}
            </button>
            <button className="mcq btn" id="d">
              {questions[this.state.i].optionD}
            </button>
          </div>
          <div className="controllers">
            <button className="btn prev" onClick={prev}>
              Previous
            </button>
            <button className="btn nxt" onClick={next}>
              Next
            </button>
            <button className="btn quit" onCanPlay={quit}>
              Quit
            </button>
          </div>
        </div>
      </div>
    );
  }
}
